var opete = "";
	  
for (var i = 0; i < opt.length; i++) {
	var nama = opt[i].nama,
		konten = opt[i].konten,
		kategori = opt[i].kategori;
	
	opete += "<div class='opt-card' data-nama='" + nama + "' data-kategori='" + kategori + "'><a href='' class='button-opt' href='' data-bs-toggle='modal' data-bs-target='#opete' data-obj='"+i+"' style='color:black'><h4><b>"+ nama +"</b></h4><p>"+ kategori +"</p><hr></a></div>";
}

$("#list-opt").html(opete);

var optLength = opt.length;
var thisElement2 = 0;

function innerContent2(content){
  $(".inner-konten-opt").html(opt[content].konten);
};

$(".button-opt").click(function(e){
  e.preventDefault();
  thisElement2 = $(this).attr("data-obj");
  innerContent2(thisElement2);
});

$(function() {
  $("#cariOPT").on("keyup", function() {
    var value = $(this).val().toLowerCase();
    $("#list-opt .opt-card").filter(function() {
        $("#list-opt").show();
        $(this).toggle($(this).text().toLowerCase().indexOf(value) > -1)
      });
  });
});

$(document).ready(function(){
  var list = $("#list-opt .opt-card");
  var numToShow = 20;
  var button = $("#opt-lainnya");
  var numInList = list.length;
  list.hide();
  if (numInList > numToShow) {
    button.show();
  }
  list.slice(0, numToShow).show();

  button.click(function(){
      var showing = list.filter(':visible').length;
      list.slice(showing - 1, showing + numToShow).fadeIn();
      var nowShowing = list.filter(':visible').length;
      if (nowShowing >= numInList) {
        button.hide();
      }
  });
});